#!/bin/bash

echo "Configuring vault server..."

export VAULT_TOKEN="00000000-0000-0000-0000-000000000000"
export VAULT_ADDR="http://localhost:8200"

echo "Enabling secret engine..."

# Enable the key-value secrets engine at the dev/ path
vault secrets enable -path=dev kv-v2

echo "Adding secrets..."

vault kv put dev/order-service spring.datasource.username=order spring.datasource.password=postgres
vault kv put dev/product-service spring.data.mongodb.username=product spring.data.mongodb.password=password
vault kv put dev/user-service jwt.private- spring.datasource.username=user spring.datasource.password=postgres
vault kv put dev/jwt-configuration jwt.public-